public class for_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i,j;
		for(i=11; i<=13; i++)
		{
			System.out.println("Table of "+i+" is: ");
			for(j=1; j<=10; j++)
			{
				System.out.print(i*j+" ");
			}
			System.out.println();
		}
		

	}

}
